import Page1 from './Page1';
import Page2 from './Page2';
import {NativeModules} from 'react-native';
import { SRNApp } from './../../src/index';
import { SRNConfig, SRNStore, SRNFetch } from "./../../src/index";
/**
 * 加载初始化配置（各个环境）
 */
SRNConfig.load({
    default: require('../config/default.config').default,
    dev: require('../config/dev.config').default,
    prepub: require('../config/prepub.config').default,
    prod: require('../config/prod.config').default
});
// 初始化实例
var app = new SRNApp();
let routes = {
    '/page1': Page1,
    '/page2': Page2
};
app.setNavigation({
    headerStyle: {
        backgroundColor:"red"
    }
})
// 初始化路由设置
app.init(routes, '/page1');
// 初始化 Sentry
app.initSentry(SRNConfig.sentry.version, SRNConfig.sentry.dsn);

// 可使用DevTools调试网络请求（需启用 ORS 资源跨域共享）
if (__DEV__) {
    GLOBAL.XMLHttpRequest =
        GLOBAL.originalXMLHttpRequest || GLOBAL.XMLHttpRequest;
}

