import CheckType from './CheckType';

export default function (value, type) {
    let shouldWarn = false;
    if(typeof(value) === 'undefined' || value === null) {
        return;
    }
    if(type.constructor && type.constructor.name == 'RegExp') {
        if(!type.test(value)) {
            shouldWarn = true;
        }
    }
    switch(type) {
        case CheckType.String:
            if(typeof(value) !== 'string') {
                shouldWarn = true;
            }
            break;
        case CheckType.Object:
            if(typeof(value) !== 'object') {
                shouldWarn = true;
            }
            break;
        case CheckType.Array:
            if(!Array.isArray(value)) {
                shouldWarn = true;
            }
            break;
        case CheckType.Number:
            if(typeof(value) !== 'number') {
                shouldWarn = true;
            }
            break;
        case CheckType.Boolean:
            if(typeof(value) !== 'boolean') {
                shouldWarn = true;
            }
            break;

        case CheckType.Any:
            break;
    }
    return shouldWarn;
};