
export default function price(value,type){
        let matches = (value + '').match(/([0-9.]*)_\$(.+)$/);
        let v;
        if(matches) {
            let server_price = matches[1];
            let unit = matches[2];
            let real_yuan = 0;
            switch (unit) {
                case 'y':
                    real_yuan = server_price * 1;
                    break;
                case 'wy':
                    real_yuan = server_price * 10000;
                    break;
                case 'f':
                    real_yuan = server_price / 10;
                    break;
                default:
                    real_yuan = server_price * 1;
                    break;
            }
            switch(type) {
                case 'wy':
                    v = Number(real_yuan / 10000);
                    break;
                case 'y':
                    v = Number(real_yuan * 1);
                    break;
                case 'f':
                    v = Number(real_yuan * 10);
                    break;
                default:
                    v = Number(real_yuan);
                    break;
            }
        } else {
            v = Number(value);
        }
        return v;
};