import { NativeModules } from 'react-native';
const scheme = NativeModules.SCCRNAppData.appScheme + '://';
// 协议名称
exports.loginScheme = scheme + 'open.present/login';
// 状态结果集
exports.State = {
    SUCCESS: 'success',
    FAIL: 'fail',
    CANCEL: 'cancel'
};

// 调用函数基类
exports.SendModule = function(protocol, data = {}) {
    return new Promise((resolve, reject) => {
        NativeModules.SCCRNModuleBridge.ModuleBridge({
            protocol: scheme + protocol,
            data
        }, (err, res) => {
            if(err) {
                reject(err);
                return;
            }
            resolve(res);
        });
    });
}