import { SendModule } from '../const';
import { NativeModules } from 'react-native';
import { SRNEventManager } from '@souche-f2e/srn-util';
/** 
 * 从APP获取数据
 */
export default {
    getAppData() {
        return new Promise((resolve, reject) => {
            NativeModules.SCCRNAppData.getAppData(function(err, data) {
                if(err) {
                    reject(err);
                    return;
                }
                SRNEventManager.emit('global_app_data_change', data);
                resolve(data);
            });
        });
    },
    getGPSData() {
        return SendModule('open/getGPS');
    },
    getDeviceData() {
        return SendModule('open/wirelessDeviceData');
    }
}