import { NativeModules } from 'react-native';

export default {
    closeView(data) {
        if (data && typeof data === 'object') {
            NativeModules.SCCRNControlBridge.SCCRNTriggleCallBack(data, (error, events) => {
            });
        } else {
            NativeModules.SCCRNControlBridge.SCCRNTriggleCallBack({}, (error, events) => {
            });
        }
        NativeModules.SCCRNControlBridge.SCCRNCloseView({}, (error, events) => {
        });
    }
};