import { SendModule } from '../const';
/** 
 * 分享类 
 */
export default {
    share(data) {
        return SendModule('open/shareHandler', data).then(res => {
            if(+res.shareResult === 1) {
                return Promise.resolve(res.platform);
            }
            return Promise.reject(res);
        });
    },

    newShare(data) {
        return SendModule('open/newShareHandler', data).then(res => {
            if(+res.shareResult === 1) {
                return Promise.resolve(res.platform);
            }
            return Promise.reject(res);
        });
    },
}