import React from 'react';
import NavHelper from './../src/index';

import {
    StyleSheet,
    Text,
    Image,
    ScrollView,
    TouchableOpacity
} from 'react-native';

class Demo5 extends React.Component {

    static navigation = {
        title:{
            text: "第1页"
        },
        right: {
            text:"aaa",
            onPress: function(emitter, sceneProps) {
                emitter.emit('share', {command: '分享'})
            }
        }
    }
    constructor(props){
        super(props);
        console.log(props);

    }
    componentDidMount () {
        // console.log(this.route)
        // console.log(this.props)
        this.emitter.on('share', data => {
            console.log(data)
        })
        setTimeout(()=>{
            this.setNavigation({
                title:{
                    text:'变化title'
                }
            })
        },1000)

        this.emitter.on('view_disappear', data => {
            console.log('1 disappear')
        })
        this.emitter.on('view_appear', data => {
            console.log('1 appear')
        })
    }
    render() {
        return (
            <ScrollView style={styles.container}>
                <Text style={styles.text}>第1页</Text>
                <TouchableOpacity onPress={()=>{
                    NavHelper.pop('first',{
                        from:5
                    })
                }}>
                    <Text>回到跳转第1页</Text>
                </TouchableOpacity>

            </ScrollView>

        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 16,
        flexDirection: 'column'
    },
    text: {
        marginBottom: 16,
        lineHeight: 20
    },
});


export default Demo5;