import React from 'react';
import {
    Image,
    TouchableOpacity,
    StyleSheet,
    NativeModules
} from 'react-native';

const AppData = NativeModules.SCCRNAppData;
const isDestiny = (AppData && AppData.appName === 'destiny');
const leftImageUri = isDestiny
    ? "https://assets.souche.com/assets/sccimg/common/back@3x.png"
    : "data:image/png;base64,";
const _LeftArrow = props => {
    return (
        <Image source={{uri: leftImageUri}} style={[styles.leftItemImage, props.style, props.leftArrowStyle]}/>
    );
};

const LeftArrow = props => {
    return (
        <TouchableOpacity activeOpacity={1} style={styles.container} onPress={props.onPress}>
            <_LeftArrow {...props} />
        </TouchableOpacity>
    );
};
const marginLeft = isDestiny ? 1 : 10;

const styles = StyleSheet.create({
    container: {
        justifyContent: 'center',
        alignItems: 'center',
        paddingLeft: 6,
        paddingRight: 6,
        marginLeft: -16,
    },
    leftItemImage:{
        width: 24,
        height: 24,
        marginLeft: marginLeft,
        alignSelf: 'center'
    },
});

export default LeftArrow;