'use strict';

const execSync = require('child_process').execSync;

module.exports = function (done) {
    const registry = 'http://registry.npm.souche-inc.com';

    try {
        execSync('npm rm babel-preset-react-native');
    } catch (e) {

    }

    try {
        execSync(`npm install -D -E babel-preset-react-native@2.1.0 --registry=${registry}`);
    } catch (e) {

    }

    if (done) {
        // eslint-disable-next-line
        done();
    }
};
