'use strict';

const path = require('path');

const shell = require('shelljs');

const globalData = require('./global_data');

const moveExample = {
    run: function (done) {
        shell.mv(path.resolve(globalData.RN_TEMPLATE_FOLDER, './examples'), globalData.PROJECT_ROOT);

        if (done) {
            // eslint-disable-next-line
            done();
        }
    }
};

module.exports = moveExample;
