#!/usr/bin/env node

const path = require('path');
const fs = require('fs');

const customThemePath = path.resolve(process.cwd(), './srn-ui.theme.js');
const sourceThemePath = path.resolve(process.cwd(), './node_modules/@souche-ui/srn-ui/src/theme/extend.js');

if (fs.existsSync(customThemePath)) {
    const extendConfigContnet = fs.readFileSync(customThemePath, { encoding: 'utf8' });

    if (fs.existsSync(sourceThemePath)) {
        fs.writeFileSync(sourceThemePath, extendConfigContnet);
    } else {
        console.warn('找不到 ' + sourceThemePath + ' 文件');
    }
} else {
    console.warn('项目根目录下缺少 srn-ui.theme.js 文件，无法应用自定义主题');
}
