import React from 'react';
import {
    Text,
    View,
    StyleSheet,
} from 'react-native';

import FontSize from '../../const/FontSize';
import theme from '../../theme';

export default function Counter(props) {
    return (
        <View style={[styles.container, props.style, {borderColor: props.tintColor || theme('color_text_caption')}]}>
            <Text style={[styles.text, {color: props.tintColor || theme('color_text_body')}]}>{props.children}</Text>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        overflow: 'hidden',
        borderWidth: StyleSheet.hairlineWidth,
        borderColor: theme('color_text_caption'),
        borderRadius: 6,
        height: 12,
        minWidth: 12,
        paddingHorizontal: 2,
        alignItems: 'center',
        justifyContent: 'center',
    },
    text: {
        fontSize: FontSize.T2,
        color: theme('color_text_body'),
    },
});