import React, {Component} from 'react';
import {View} from 'react-native';
import PropTypes from 'prop-types';

export default class WhiteSpace extends Component{
    sizeMap = {
        l: 16,
        s: 12
    };
    render(){
        const {size, style} = this.props;
        let height = 16;
        if (size || size === 0){
            const typeSize = typeof size;
            if (typeSize === 'string') {
                height = this.sizeMap[size] || 16;
            } else if (typeSize === 'number'){
                height = size;
            } else {
                height = 16;
            }
        } else {
            height = 16;
        }
        return (
            <View style={[{height: height}, style]}/>
        );
    }
}

WhiteSpace.propTypes = {
    size: PropTypes.oneOfType([
        PropTypes.oneOf(['l', 's']),
        PropTypes.number,
    ]),
    style: View.propTypes.style,
};

WhiteSpace.defaultProps = {
    size: 'l'
};