import React, {Component} from 'react';
import {View} from 'react-native';
import PropTypes from 'prop-types';

export default class WingBlank extends Component{
    render(){
        const {style, children, size} = this.props;
        const padding = size || 16;
        return (
            <View style={[{paddingLeft: padding, paddingRight: padding}, style]}>
                {children}
            </View>
        );
    }
}

WingBlank.propTypes = {
    size: PropTypes.oneOfType([
        PropTypes.number,
    ]),
    style: View.propTypes.style,
};

WingBlank.defaultProps = {
    size: 16
};