import {
    Dimensions,
    Platform,
} from 'react-native';

const X_HEIGHT = 812;
const X_WIDTH = 375;

export function isIPhoneX() {
    const WINDOW_HEIGHT = Dimensions.get('window').height;
    const WINDOW_WIDTH = Dimensions.get('window').width;
    return (
        Platform.OS === 'ios' &&
        ((WINDOW_HEIGHT === X_HEIGHT && WINDOW_WIDTH === X_WIDTH) ||
        (WINDOW_HEIGHT === X_WIDTH && WINDOW_WIDTH === X_HEIGHT))
    );
}