export function omit(obj, fields) {
    var shallowCopy = Object.assign({}, obj);
    for (var i = 0; i < fields.length; i++) {
      var key = fields[i];
      delete shallowCopy[key];
    }
    return shallowCopy;
}

export function containHalfWidthChar(str) {
  return /[\s\w`~!@#$%^&*()-_=+\[{\]}\\|;:'",<.>/?]+/.test(str);
}