import {
    StyleSheet
} from 'react-native';
import _ from 'lodash';

import {NativeModules} from 'react-native';
const AppData = NativeModules.SCCRNAppData;
const appName = AppData && AppData.appName ? AppData.appName.toLowerCase() : '';

const styleUtil = {
    create(_platformStyles) {
        let _styles = _platformStyles.common || {};
        if (_platformStyles[appName]) {
            _styles = _.extend(_styles, _platformStyles[appName]);
        }
        return StyleSheet.create(_styles);
    }
};

export default styleUtil;